import React from 'react';
import { useParams } from 'react-router-dom';

const dummyHotels = [
  {
    id: '1',
    name: 'Ocean View Hotel',
    location: 'Miami, FL',
    price: '$120/night',
    image: '/images/beach-hotel.jpg',
    rating: 4.5,
    description: 'Enjoy the beautiful ocean view from your room and relax on the beach.',
  },
  {
    id: '2',
    name: 'Mountain Escape Lodge',
    location: 'Denver, CO',
    price: '$90/night',
    image: 'https://source.unsplash.com/400x300/?hotel,mountain',
    rating: 4.3,
    description: 'A peaceful retreat in the mountains with hiking trails and fresh air.',
  },
  {
    id: '3',
    name: 'City Central Inn',
    location: 'New York, NY',
    price: '$150/night',
    image: 'https://source.unsplash.com/400x300/?hotel,city',
    rating: 4.7,
    description: 'Located in the heart of the city, close to shopping, dining, and attractions.',
  },
];

const HotelDetails = () => {
  const { id } = useParams();
  const hotel = dummyHotels.find(h => h.id === id);

  if (!hotel) return <p>Hotel not found</p>;

  return (
    <div className="hotel-details">
      <img src={hotel.image} alt={hotel.name} className="hotel-detail-image" />
      <h2>{hotel.name}</h2>
      <p><strong>Location:</strong> {hotel.location}</p>
      <p><strong>Price:</strong> {hotel.price}</p>
      <p><strong>Rating:</strong> {hotel.rating} ‚≠ê</p>
      <p>{hotel.description}</p>

      <form className="booking-form">
        <h3>Book this hotel</h3>
        <label>Guests:</label>
        <input type="number" min="1" defaultValue="1" />
        <label>Rooms:</label>
        <input type="number" min="1" defaultValue="1" />
        <button type="submit">Confirm Booking</button>
      </form>
    </div>
  );
};

export default HotelDetails;
